void foo(int *__counted_by(count) x, unsigned count) {
	*x++ = 0;
    #ifdef WITH
    // NOTE: It's necessary to exclude the count update with
    // indirect_count_update disabled otherwise we'll get diagnostics about
    // the a missing assignment to `x`.
    count--;
    #endif
}

