// WITHOUT-SAME: ptr nocapture noundef writeonly [[X:%.*]], i32 noundef [[COUNT:%.*]]) local_unnamed_addr #[[ATTR0]] {
// WITHOUT-NEXT:  [[ENTRY:.*:]]
